import React, { useState, useEffect } from 'react'

const Timer = () => {
    const [counter, setCounter] = useState(5);

    useEffect(() => {
        const timer =
            counter > 0 && setInterval(() => setCounter(counter - 1), 1000);
        return () => clearInterval(timer);
    }, [counter]);


    return (
        <div>
            <div>Countdown: {counter}</div>
        </div>
    );
}
export default Timer;
