import React, { useState, useEffect } from 'react'
import Player1 from "./player1.js";
import Player2 from "./player2.js";
import Layout from "./layout.js";
import Game from "./game.js";
import useSound from 'use-sound';
import DbzMain from './sounds/dbzMain.mp3'


function App() {
  const [showGamePlayer1, setShowGamePlayer1] = useState(false);
  const [showGamePlayer2, setShowGamePlayer2] = useState(false);

  const [click, setClick] = useState(true);

  const [playMusic, { stop }] = useSound(DbzMain, {
    interrupt: true,
    volume: 0.5
  });

  const onClick = () => {
    if (click === true) {
      playMusic();
      setClick(!click);
    }
    else if (click === false) {
      stop();
      setClick(!click);
    }
  }

  return (
    <div>
      <Layout />
      <Game />
      <button className="badge bg-dark" onClick={onClick} style={{ color: "white" }}>Click me !</button>

    </div>
  );
}

export default App;
