import vegeta from './images/vegeta.png'
import gohanssj2 from './images/gohanssj2.webp'
import gotenksssj3 from './images/gotenksssj3.png'
import piccolo from './images/piccolo.png'
import "./App.css"

const Player1 = () => {
    const [Character, setCharacter] = useState([
        <img className="goku" src={goku} alt="goku" id="1"/>,
        <img className="vegeta" src={vegeta} alt="vegeta"  id="2"/>,
        <img className="gohanssj2" src={gohanssj2} alt="gohanssj2"  id="3"/>,
        <img className="gotenksssj3" src={gotenksssj3} alt="gotenksssj3"  id="4"/>,
        <img className="piccolo" src={piccolo} alt="piccolo"  id="5"/>
    ])

    const [showCharacter, setShowCharacter] = useState(1)

    const onClick = () => {
        if (showCharacter.id == 2)
        setShowCharacter(true)
    }
    return (
        <div>
            {sho}
            {showCharacter.map((p, i) => (<div key={i}>{p}</div>))}
            <button className="btn btn-primary" onClick={onClick}>Next</button>

            




        </div>
    )
}
export default Player1;
