import React, { useState, useEffect } from 'react'

const Game = () => {
    const [click, setClick] = useState(0);
    const [show, setShow] = useState(true);
    const [test, setTest] = useState(97);
    const IncrementItem = () => {
        setClick(click + 1);


    }
    const DecreaseItem = () => {
        setClick(click - 1);
    }
    const ToggleClick = () => {
        setShow(!show);
    }


    const enterPressed = (event) => {
        var code = event.keyCode || event.which;
        
        console.log("code : " + code);
        console.log("test : " + code);
        if (test === code) {
            if (code === 97) { //13 is the enter keycode
                setTest = 101;
                setClick(click + 1);
            }
        }
        else if (test === code) {
            if (code === 101) {
                test = 97;
                setClick(click + 1);
            }
        }
    }
    const enterPressed2 = (event) => {
        var code2 = event.keyCode || event.which;
        console.log(code2);
        if (code2 === 101) { //13 is the enter keycode
            setClick(click + 1);
        }
    }

    return (
        <div>
            <button onKeyPress={enterPressed}>Start</button>

            { show ? <h2>{click}</h2> : ''}

        </div>
    )
}
export default Game;
