import React, { useState, useEffect } from 'react'

const Game = () => {
    const [click, setClick] = useState(0);
    const [show, setShow] = useState(true);

    const IncrementItem = () => {
            setClick(click + 1);
        

    }
    const DecreaseItem = () => {
        setClick(click - 1);
    }
    const ToggleClick = () => {
        setShow(!show);
    }
    const enterPressed = (event) => {
        let code = event.keyCode || event.which;
        console.log(code);
        if (code === 97 ) { //13 is the enter keycode
            setClick(click + 1);
        }
    }

    return (
        <div>
            <button onKeyPress={enterPressed}>Start</button>
         
            { show ? <h2>{click}</h2> : ''}

        </div>
    )
}
export default Game;
