import React, { useState, useEffect, useCallback, useMemo } from 'react'

const Game = () => {
    const [press, setPress] = useState(0);
    const [press2, setPress2] = useState(0);

    const [show, setShow] = useState(true);

    const [testA, setTestA] = useState(["a", "e"]);
    const [testB, setTestB] = useState(["i", "p"]);

    const [counter, setCounter] = useState(5);

    useEffect(() => {
        const timer =
            counter > 0 && setInterval(() => setCounter(counter - 1), 1000);
        return () => clearInterval(timer);
    }, [counter]);

    useEffect(() => {
        counter === 0 && document.addEventListener('keypress', keyPressedA, keyPressedB)
    }, [counter])

    

    var countA = 0;
    const keyPressedA = (event) => {
        var codeA = event.key;

        console.log("code : " + codeA);
        console.log("countA : " + countA);
        console.log("testA : " + testA[countA]);
        if (testA[countA] === code && code === 'a') { //97 keycode for the key 'a'
            countA = 1;
            setPress(press => press + 1);
            console.log("dans le 1ere boucle")
            console.log("countA dans le 1ere boucle: " + countA);

        }
        else if (testA[countA] === code && code === 'e') { //101 keycode for the 'e'
            countA = 0;
            setPress(press => press + 1);
            console.log("dans le 2eme boucle")
            console.log("countA dans le 2eme boucle : " + countA);

        }
    }

    var countB = 0;
    const keyPressedB = (event) => {
        var codeB = event.key;

        console.log("code : " + code);
        console.log("countB : " + countB);
        console.log("testB : " + testB[countB]);
        if (testB[countB] === code && code === 'i') { //97 keycode for the key 'a'
            countB = 1;
            setPress2(press2 => press2 + 1);
            console.log("dans le 1ere boucle")
            console.log("countB dans le 1ere boucle: " + countB);

        }
        else if (testB[countB] === code && code === 'p') { //101 keycode for the 'e'
            countB = 0;
            setPress2(press2 => press2 + 1);
            console.log("dans le 2eme boucle")
            console.log("countB dans le 2eme boucle : " + countB);

        }
    }




    // function useClick(onClick, setVal, val) {
    //     React.useEffect(() => {
    //       console.log('Click');
    //       setVal(val + 1);
    //     }, [onClick]);
    //   }

    //   const Home = () => {
    //     const [val, setVal] = React.useState(0);
