    //       console.log('go set');
    //       setVal(val + 1);
    //     } , [setVal, val]);
    //     useClick(incrementOnClick, setVal, val);


    //     const { useEffect, useState, useCallback } = React;

    //     function useClick(onClick, setVal, val) {
    //       useEffect(() => {
    //         console.log("Click");
    //         setVal(val + 1);
    //       }, [onClick]);
    //     }

    //     const Home = () => {
    //       const [val, setVal] = useState(0);
    //       const incrementOnClick = useCallback(() => {
    //         console.log("go set");
    //         setVal(val => val + 1);
    //       }, []);

    //       useClick(incrementOnClick, setVal, val);

    //       return (
    //         <div>
    //           <div>{val}</div>
    //           <button onClick={incrementOnClick}>Click me</button>
    //         </div>
    //       );
    //     };

    //     ReactDOM.render(<Home />, document.getElementById("root"));


    return (
        <div>
            <button onKeyPress={keyPressedA}>Start</button>

            { show ? <h2>{press}</h2> : ''}


            { show ? <h2>{press2}</h2> : ''}
            <div>Countdown: {counter}</div>
            {/* {document.addEventListener('keypress', (event) => {console.log('event.key');
                setPress(press + 1); })} */}



            {/* <div>{val}</div>
          <button onClick={incrementOnClick}>Click me</button> */}
        </div>
    )
}
