import React, { useState, useEffect } from 'react'

const Game = () => {
    const [press, setPress] = useState(0);
    const [show, setShow] = useState(true);
    const [test, setTest] = useState(97);

    const [press2, setPress2] = useState(0);
    const [show2, setShow2] = useState(true);
    const [test2, setTest2] = useState(55);


    const [counter, setCounter] = useState(5);

    useEffect(() => {
        const timer =
            counter > 0 && setInterval(() => setCounter(counter - 1), 1000);
        return () => clearInterval(timer);
    }, [counter]);

    useEffect(() => {
        counter === 0 && document.addEventListener('keypress', (event) => {
            if (event.key === 'a' || event.key === 'e') {
                event.preventDefault();
                keyPressed(event);
            }
        })
        return () => document.addEventListener('keyup', onKeyUp)
    }, [counter, test, press])

    const [currentKeysPressed, setCurrentKeyPressed] = useState({});


    const keyPressed = (event) => {
        setCurrentKeyPressed(currentKeysPressed[event.key] = 'a');
        var code = event.keyCode || event.which;
        console.log("code : " + code);
        console.log("test : " + test);
        if (test === code && code === 97) { //97 keycode for the key 'a'
            setTest(101);
            setPress(press + 1);
            console.log("dans le 1ere boucle")

        }
        else if (test === code && code === 101) { //101 keycode for the 'e'
            setTest(97);
            setPress(press + 1);
            console.log("dans le 2eme boucle")

        }
    }

    const onKeyUp = (event) => {
        setCurrentKeyPressed(currentKeysPressed[event.key] = false);

    }
    const keyPressed2 = (event) => {
        event.preventDefault();
        var code = event.keyCode || event.which;
        console.log("code : " + code);
        //console.log("test : " + test);
        if (test2 === code && code === 55) { //97 keycode for the key 'a'
            setTest2(57);
            setPress2(press2 + 1);
        }
        else if (test2 === code && code === 57) { //101 keycode for the 'e'
            setTest2(55);
            setPress2(press2 + 1);
        }
    }


    return (
        <div>
            <button onKeyPress={keyPressed2}>Start</button>

            { show ? <h2>{press}</h2> : ''}


            { show2 ? <h2>{press2}</h2> : ''}
            <div>Countdown: {counter}</div>
            {}
        </div>
    )
}
export default Game;
