import React, { useState, useEffect } from 'react'
import useKeyPress from "./useKeyPress"

const Game = () => {
    const [click, setClick] = useState(0);
    const [show, setShow] = useState(true);
    const a = useKeyPress('a');

    const IncrementItem = () => {
        setClick(click + 1);
    }
    const DecreaseItem = () => {
        setClick(click - 1);
    }
    const ToggleClick = () => {
        setShow(!show);
    }

    return (
        <div>
            <button onKeyPress={IncrementItem}>Click to increment by 1</button>
            <button onClick={DecreaseItem}>Click to decrease by 1</button>
            <button onClick={ToggleClick}>
                {show ? 'Hide number' : 'Show number'}
            </button>
            { show ? <h2>{click}</h2> : ''}

            {}
        </div>
    )
}
export default Game;
