import React, { useState, useEffect } from 'react'
import goku from './images/goku.png'
import vegeta from './images/vegeta.png'
import gohanssj2 from './images/gohanssj2.webp'
import gotenksssj3 from './images/gotenksssj3.png'
import piccolo from './images/piccolo.png'
import "./App.css"
import "./start.css"
import "./validation.css"
import Validation from "./validation.js"


const Player1 = () => {
    const [Character, setCharacter] = useState([
        { id: 0, content: <img className="goku" src={goku} alt="goku" />, name: "Goku" },
        { id: 1, content: <img className="vegeta" src={vegeta} alt="vegeta" />, name: "Vegeta" },
        { id: 2, content: <img className="gohanssj2" src={gohanssj2} alt="gohanssj2" />, name: "Gohan ado super saiyan 2" },
        { id: 3, content: <img className="gotenksssj3" src={gotenksssj3} alt="gotenksssj3" />, name: "Gotenks super saiyan 3" },
        { id: 4, content: <img className="piccolo" src={piccolo} alt="piccolo" />, name: "Piccolo" }
    ])

    const [showCharacter, setShowCharacter] = useState(0)

    const onClick = () => {
        if (Character[showCharacter].id !== 4) {
            setShowCharacter(showCharacter + 1);
        }
        else {
            setShowCharacter(0);
        }
    }

    const onClick2 = () => {
        if (Character[showCharacter].id === 0) {
            setShowCharacter(4);
        }
        else {
            setShowCharacter(showCharacter - 1);
        }
    }
    return (
        <div className="container">
            <div className="row">
                <div className="col text-center mb-3">
                    {Character[showCharacter].name}
                </div>
            </div>
            <div className="row">
                <div className="col align-self-center">
                    <span className="chevron left" onClick={onClick2}></span>
                </div>
                <div className="col mb-3 ml-3">
                    {Character[showCharacter].content}
                </div>
                <div className="col align-self-center">
                    <span className="chevron right" onClick={onClick}></span>
                </div>
            </div>
            <div className="row">
                <div className="col text-center">
                    <div className="btn style-1">
                        <Validation />
                    </div>
                </div>
            </div>
        </div>


    )
}
export default Player1;
