import React, { useState, useEffect } from 'react'
import Player1 from "./player1.js";
import Player2 from "./player2.js";
import Layout from "./layout.js";
import Game from "./game.js";
import useSound from 'use-sound';
import DbzMain from './sounds/dbzMain.mp3'


function App() {
  const [showGamePlayer1, setShowGamePlayer1] = useState(false);
  const [showGamePlayer2, setShowGamePlayer2] = useState(false);

  const [play] = useSound()
  useSound(DbzMain)

  return (
    <div>
      {document.readyState(play)}
      <Layout />
      <div  className="container-fluid" >
        <div className="row">
          <div className="col-5 text-center">
            <Player1 showGamePlayer1={setShowGamePlayer1} />
          </div>
          <div className="col-2 text-center">
            {showGamePlayer1 && showGamePlayer2 && alert("Left player press A and E to increase your counter when the count down is over.")}
            {showGamePlayer1 && showGamePlayer2 && alert("Right player press I and P to increase your counter when the count down is over.")}
            {showGamePlayer1 && showGamePlayer2 && <Game />}
          </div>
          <div className="col-5 text-center">
            <Player2 showGamePlayer2={setShowGamePlayer2} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
