import React, { useState, useEffect } from 'react'

const Game = () => {
    const [click, setClick] = useState(0);
    const [show, setShow] = useState(true);
    const [test, setTest] = useState(97);

    const [click2, setClick2] = useState(0);
    const [show2, setShow2] = useState(true);
    const [test2, setTest2] = useState(55);


    const [counter, setCounter] = useState(5);

    useEffect(() => {
        const timer =
            counter > 0 && setInterval(() => setCounter(counter - 1), 1000);
        return () => clearInterval(timer);
    }, [counter]);

    useEffect(() => {
      const timer = counter === 0 && document.addEventListener('keypress', (event) => { if (event.key === 'a' || event.key === 'e') { enterPressed(event); } })
      return () => clearInterval(timer)
    }, [counter, test, click])

    const enterPressed = (event) => {
        var code = event.keyCode || event.which;
        console.log("code : " + code);
        console.log("test : " + test);
        if (test === code && code === 97) { //97 keycode for the key 'a'
            setTest(101);
            setClick(click + 1);
            console.log("dans le 1ere boucle")

        }
        else if (test === code && code === 101) { //101 keycode for the 'e'
            setTest(97);
            setClick(click + 1);
            console.log("dans le 2eme boucle")

        }
    }

    const enterPressed2 = (event) => {
        event.preventDefault();
        var code = event.keyCode || event.which;
        console.log("code : " + code);
        //console.log("test : " + test);
        if (test2 === code && code === 55) { //97 keycode for the key 'a'
            setTest2(57);
            setClick2(click2 + 1);
        }
        else if (test2 === code && code === 57) { //101 keycode for the 'e'
            setTest2(55);
            setClick2(click2 + 1);
        }
    }


    return (
        <div>
            <button onKeyPress={enterPressed2}>Start</button>

            { show ? <h2>{click}</h2> : ''}


            { show2 ? <h2>{click2}</h2> : ''}
            <div>Countdown: {counter}</div>
            {}
        </div>
    )
}
export default Game;
