import React from 'react'
import "./App.css"
import dbzvs from './images/dbzvs.png'

const Layout = () => {

    const [click, setClick] = useState(true);




    const onClick = () => {
        if (click === true) {
            playMusic();
            setClick(!click);
        }
        else if (click === false) {
            stop();
            setClick(!click);
        }
    }

    return (
        <div className="jumbotron">
                        <button className="badge bg-dark" onClick={onClick} style={{ color: "white", top: "0", position: "sticky" }}>Click me !</button>
            <div className="text-center">
                <img style={{ transform: "translate(0%, -30%)" }} className="dbzvs" src={dbzvs} alt="dbzvs" />
            </div>
        </div>
    )
}
export default Layout;
