import React, { useState, useEffect, useCallback, useMemo } from 'react'

const Game = () => {
    const [press, setPress] = useState(0);

    const [show, setShow] = useState(true);
    const [test, setTest] = useState(["a", "e"]);

    const [press2, setPress2] = useState(0);
    const [show2, setShow2] = useState(true);
    const [test2, setTest2] = useState(55);


    const [counter, setCounter] = useState(5);

    useEffect(() => {
        const timer =
            counter > 0 && setInterval(() => setCounter(counter - 1), 1000);
        return () => clearInterval(timer);
    }, [counter]);

    useEffect(() => {
        counter === 0 && document.addEventListener('keypress', keyPressedA)
    }, [counter])

    // const [currentKeysPressed, setCurrentKeyPressed] = useState({});

    var count = 0;
    const keyPressedA = (event) => {
        var code = event.key

        console.log("code : " + code);
        console.log("count : " + count);
        console.log("test : " + test[count]);
        if (test[count] === code && code === 'a') { //97 keycode for the key 'a'
            count = 1;
            setPress(press => press + 1);
            console.log("dans le 1ere boucle")
            console.log("count dans le 1ere boucle: " + count);

        }
        else if (test[count] === code && code === 'e') { //101 keycode for the 'e'
            count = 0;
            setPress(press => press + 1);
            console.log("dans le 2eme boucle")
            console.log("count dans le 2eme boucle : " + count);

        }
    }

    const keyPressedB = (event) => {
        var code = event.key
        console.log("code : " + code);
        setPress(press + 1);
    }
    const onKeyUp = (event) => {
        // setCurrentKeyPressed(currentKeysPressed[event.key] = false);

    }
    const keyPressed2 = (event) => {
        event.preventDefault();
        var code = event.keyCode || event.which;
        console.log("code : " + code);
        //console.log("test : " + test);
        if (test2 === code && code === 55) { //97 keycode for the key 'a'
            setTest2(57);
            setPress2(press2 + 1);
        }
        else if (test2 === code && code === 57) { //101 keycode for the 'e'
            setTest2(55);
            setPress2(press2 + 1);
        }
    }



    // function useClick(onClick, setVal, val) {
    //     React.useEffect(() => {
    //       console.log('Click');
    //       setVal(val + 1);
    //     }, [onClick]);
    //   }

    //   const Home = () => {
    //     const [val, setVal] = React.useState(0);
    //     const incrementOnClick = React.useCallback(() => {
    //       console.log('go set');
    //       setVal(val + 1);
    //     } , [setVal, val]);
    //     useClick(incrementOnClick, setVal, val);


    //     const { useEffect, useState, useCallback } = React;

    //     function useClick(onClick, setVal, val) {
    //       useEffect(() => {
    //         console.log("Click");
    //         setVal(val + 1);
    //       }, [onClick]);
    //     }

    //     const Home = () => {
    //       const [val, setVal] = useState(0);
    //       const incrementOnClick = useCallback(() => {
    //         console.log("go set");
    //         setVal(val => val + 1);
    //       }, []);

    //       useClick(incrementOnClick, setVal, val);

    //       return (
    //         <div>
    //           <div>{val}</div>
    //           <button onClick={incrementOnClick}>Click me</button>
    //         </div>
    //       );
    //     };

    //     ReactDOM.render(<Home />, document.getElementById("root"));


    return (
        <div>
            <button onKeyPress={keyPressedA}>Start</button>

            { show ? <h2>{press}  {test}</h2> : ''}


            { show2 ? <h2>{press2}</h2> : ''}
            <div>Countdown: {counter}</div>
            {/* {document.addEventListener('keypress', (event) => {console.log('event.key');
                setPress(press + 1); })} */}



            {/* <div>{val}</div>
          <button onClick={incrementOnClick}>Click me</button> */}
        </div>
    )
}
export default React.memo(Game);
