import React, { useState, useEffect } from 'react'
import "./start.css"
import useSound from 'use-sound';
import smash from './sounds/smash.m4a'
import magnetism from './sounds/magnetism.mp3'
import Player1 from "./player1.js";
import Player2 from "./player2.js";
import Modal from 'react-bootstrap/Modal'

const Game = () => {
    const [press, setPress] = useState(100);
    const [press2, setPress2] = useState(100);

    const [isActive, setIsActive] = useState(false);

    const [show] = useState(true);

    const [countDown, setCountDown] = useState(5);

    const [mouse, setMouse] = useState(true);

    const [showGamePlayer1, setShowGamePlayer1] = useState(false);
    const [showGamePlayer2, setShowGamePlayer2] = useState(false);

    const [show2, setShow2] = useState(false);

    const handleClose = () => setShow2(false);
    const handleShow = () => setShow2(true);


    var testA = "a";
    var testB = "i";

    useEffect(() => {
        const timer =
            isActive === true && countDown > 0 && setInterval(() => setCountDown(countDown - 1), 1000);
        return () => clearInterval(timer);
    }, [countDown, isActive]);

    useEffect(() => {
        countDown === 0 && document.addEventListener('keypress', keyPressedA)
    }, [countDown])

    useEffect(() => {
        countDown === 0 && document.addEventListener('keypress', keyPressedB);
    }, [countDown])

    // useEffect(() => {
    //     const timerGame =
    //         countDown === 0 && counterGame <= 9 && setInterval(() => setCounterGame(counterGame + 1), 1000);
    //     return () => clearInterval(timerGame);
    // }, [countDown, counterGame]);



    const [playMusicSmash] = useSound(smash, {
        interrupt: true,
    });

    const [playMusicMagnetism, { pause }] = useSound(magnetism, {
        interrupt: true,
    });

    const keyPressedA = (event) => {
        var code = event.key;
        if (testA === code && code === 'a') { //for the key 'a'
            testA = "e";
            setPress(press => press - 1);
        }
        else if (testA === code && code === 'e') { //for the 'e'
            testA = "a";
            setPress(press => press - 1);
        }
    }

    const keyPressedB = (event) => {
        var code = event.key;
        if (testB === code && code === 'i') { //for the key 'i'
            testB = "p";
            setPress2(press2 => press2 - 1);
        }
        else if (testB === code && code === 'p') { //for the 'p'
            testB = "i";
            setPress2(press2 => press2 - 1);
        }
    }

    const endGame = () => {
        if (press2 === 0) {
            alert("Player 2 is the winner !!!")

        } else if (press === 0) {
            alert("Player 1 is the winner !!!")

        }
    }

    const gameTriggered = () => {
        playMusicSmash();
        setIsActive(true);
    }


    const onMouse = () => {
        if (mouse === true) {
            playMusicMagnetism();
            setMouse(!mouse);
        }
        else if (mouse === false) {
            pause();
            setMouse(!mouse);
        }
    }


    return (
        <div className="col">
            {/* <div>CounterGame: {counterGame}</div> */}
            { showGamePlayer1 && showGamePlayer2 &&
                <div className="row" >
                    <div className="col-5 text-center my-auto">
                        <div className="progress">
                            <div className="progress-bar bg-danger" role="progressbar" style={{ width: `${press2}%` }} aria-valuenow="100" aria-valuemin="0" aria-valuemax="100"></div>
                        </div>
                    </div>
                    <div className="col-2 text-center my-auto" style={{ color: 'white', fontWeight: "700", fontSize: "xx-large" }}>
                        {countDown}
                    </div>
                    <div className="col-5 text-center my-auto">
                        <div className="progress">
                            <div className="progress-bar bg-danger" role="progressbar" style={{ width: `${press}%` }} aria-valuenow="100" aria-valuemin="0" aria-valuemax="100"></div>
                        </div>
                    </div>
                </div>
            }
            <div className="row">
                <div className="col-5 text-center my-auto">
                    <Player1 showGamePlayer1={setShowGamePlayer1} />
                </div>
                {/* {show ? <h2 style={{ color: 'white', fontWeight: "700", fontSize: "xx-large" }}>{press2}</h2> : ''} */}

                <div className="col-2 text-center my-auto" >
                    {showGamePlayer1 && showGamePlayer2 && <button className="start" onClick={gameTriggered} onMouseEnter={onMouse} onMouseOut={onMouse}>Start</button>}
                </div>

                <div className="col-5 text-center my-auto">
                    <Player2 showGamePlayer2={setShowGamePlayer2} />
                </div>
            </div>

            { showGamePlayer1 && showGamePlayer2 &&
                <Modal show={show2} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={handleClose}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>
            }


            { endGame()}
        </div >
    )
}
export default Game;
