import React, { useState } from 'react'
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'


const ModalP1 = () => {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
            <button className="danger" onClick={handleShow}>Launch demo modal</button>
            <Modal show={show} onHide={handleClose} aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">Results</Modal.Title>
                </Modal.Header>
                <Modal.Body>Player 1 is the winner !!!</Modal.Body>
                <Modal.Footer>
                    <Button className="danger" onClick={handleClose}>OK</Button>
                </Modal.Footer>
            </Modal>
        </>
    )
}

export default ModalP1;
