import React, { useState, useEffect } from 'react'
import "./start.css"

const Game = () => {
    const [press, setPress] = useState(0);
    const [press2, setPress2] = useState(0);

    const [isActive, setIsActive] = useState(true);

    const [show, setShow] = useState(true);

    const [countDown, setCountDown] = useState(5);
    const [counterGame, setCounterGame] = useState(0);

    const [state, setState] = useState({ seconds: 0 });

    var testA = "a";
    var testB = "i";

    // useEffect(() => {
    //     gameTriggered();
    // }, [countDown]);

    useEffect(() => {
        countDown === 0 && document.addEventListener('keypress', keyPressedA)
    }, [countDown])

    useEffect(() => {
        countDown === 0 && document.addEventListener('keypress', keyPressedB);
    }, [countDown])

    // useEffect(() => {
    //     const timerGame =
    //         countDown === 0 && counterGame <= 9 && setInterval(() => setCounterGame(counterGame + 1), 1000);
    //     return () => clearInterval(timerGame);
    // }, [countDown, counterGame]);


    const keyPressedA = (event) => {
        var code = event.key;
        if (testA === code && code === 'a') { //for the key 'a'
            testA = "e";
            setPress(press => press + 1);
        }
        else if (testA === code && code === 'e') { //for the 'e'
            testA = "a";
            setPress(press => press + 1);
        }
    }

    const keyPressedB = (event) => {
        var code = event.key;
        if (testB === code && code === 'i') { //for the key 'i'
            testB = "p";
            setPress2(press2 => press2 + 1);
        }
        else if (testB === code && code === 'p') { //for the 'p'
            testB = "i";
            setPress2(press2 => press2 + 1);
        }
    }

    const endGame = () => {
        if (press > press2 && press === 200) {
            alert("Player 1 is the winner !!!")

        } else if (press2 > press && press2 === 200) {
            alert("Player 2 is the winner !!!")

        }
    }

    // const gameTriggered = () => {
    //     const timer =
    //         countDown > 0 && setInterval(() => setCountDown(countDown - 1), 1000);
    //     return () => clearInterval(timer);
    // }

    const tick = () => {
        // start timer after button is clicked
        const interval = setInterval(() => {
          setState(prevState => ({
            seconds: prevState.seconds - 1
          }));
        }, 1000);
        return () => clearInterval(interval);
      }
    
    return (
        <div>
            {/* <div>CounterGame: {counterGame}</div> */}

            <div className="container-fluid" >
                <div className="row">
                    <div className="col-5 text-center">
                        {show ? <h2 style={{ color: 'white' }}>{press}</h2> : ''}
                    </div>
                    <div className="col-2 align-self-center" style={{ color: 'white' }}>
                        Countdown: {countDown}
                        <button className="start" onClick={tick} >Start</button>
                    </div>
                    <div className="col-5 text-center">
                        {show ? <h2 style={{ color: 'white' }}>{press2}</h2> : ''}
                    </div>
                </div>
            </div>
            {endGame()}
        </div>
    )
}
export default Game;
