import React, { useState, useEffect } from 'react'
import goku from './images/goku.png'
import vegeta from './images/vegeta.png'
import gohanssj2 from './images/gohanssj2.webp'
import gotenksssj3 from './images/gotenksssj3.png'
import piccolo from './images/piccolo.png'
import "./App.css"

const Player1 = () => {
    const [Character, setCharacter] = useState([
        { id: 0, content: <img className="goku" src={goku} alt="goku" /> },
        { id: 1, content: <img className="vegeta" src={vegeta} alt="vegeta" /> },
        { id: 2, content: <img className="gohanssj2" src={gohanssj2} alt="gohanssj2" /> },
        { id: 3, content: <img className="gotenksssj3" src={gotenksssj3} alt="gotenksssj3" /> },
        { id: 4, content: <img className="piccolo" src={piccolo} alt="piccolo" /> }
    ])

    const [showCharacter, setShowCharacter] = useState(0)

    const onClick = () => {
        if (Character[showCharacter].id != 4) {
            setShowCharacter(showCharacter + 1);
        }
        else {
            setShowCharacter(0);
        }
    }

    const onClick2 = () => {
        if (Character[showCharacter].id == 0) {
            setShowCharacter(4);
        }
        else {
            setShowCharacter(showCharacter - 1);
        }
    }
    return (
        <div>
            <button className="btn btn-primary" onClick={onClick2}>Previous</button>
            {Character[showCharacter].content}
            <button className="btn btn-primary" onClick={onClick}>Next</button>
            <button className="btn btn-primary" >Valider</button>

        </div>
    )
}
export default Player1;
